package com.javalec.function;

public class Division {
	
	//Field
		double num1; 
		double num2; 
		
		
		//Constructor
		public Division() {
			// TODO Auto-generated constructor stub
		}



		public Division(int num1, int num2) {
			super();
			this.num1 = num1;
			this.num2 = num2;
		}
		
		
		//Method
		
		public double divide() {
			return (num1/num2);
		}
	
	

}
