package com.javalec.function;

public class Multiplication {
	
	//Field
		int num1; 
		int num2; 
		
		
		//Constructor
		public Multiplication() {
			// TODO Auto-generated constructor stub
		}



		public Multiplication(int num1, int num2) {
			super();
			this.num1 = num1;
			this.num2 = num2;
		}
		
		
		//Method
		
		public int multiply() {
			return num1*num2;
		}
		
	

}
