package com.javalec.function;

public class Subtraction {

	//Field
	int num1; 
	int num2; 
	
	
	//Constructor
	public Subtraction() {
		// TODO Auto-generated constructor stub
	}



	public Subtraction(int num1, int num2) {
		super();
		this.num1 = num1;
		this.num2 = num2;
	}
	
	
	//Method
	
	public int subtract() {
		return num1-num2;
	}
	
	
	
	
}
